#include "vrmine\vr_geometry\geometry\point.h"

namespace VirtualMine
{
	namespace Geometry
	{
		CPoint::CPoint()
		{
			this->mCoors[0] = 0;
			this->mCoors[1] = 0;
			this->mCoors[2] = 0;
		}

		CPoint::~CPoint()
		{

		}

		double& CPoint::getX()
		{
			return this->mCoors[0];
		}

		double& CPoint::getY()
		{
			return this->mCoors[1];
		}

		double& CPoint::getZ()
		{
			return this->mCoors[2];
		}

		void CPoint::setX(const double& x)
		{
			this->mCoors[0] = x;
		}

		void CPoint::setY(const double& y)
		{
			this->mCoors[1] = y;
		}

		void CPoint::setZ(const double& z)
		{
			this->mCoors[2] = z;
		}

		void CPoint::setCoor(const double& x,const double& y,const double& z)
		{
			this->mCoors[0] = x;
			this->mCoors[1] = y;
			this->mCoors[2] = z;
		}

		void CPoint::toStream(VirtualMine::Core::CVRDataStream& datastream)
		{
			datastream.wirteReal(mCoors[0]);
			datastream.wirteReal(mCoors[1]);
			datastream.wirteReal(mCoors[2]);

		}
		void CPoint::fromStream(VirtualMine::Core::CVRDataStream& datastream)
		{
			datastream.readReal(mCoors[0]);
			datastream.readReal(mCoors[1]);
			datastream.readReal(mCoors[2]); 
		}


	}
}
