#include "vrmine\vr_spatiallayer\layermanager.h"
#include "vrmine\vr_spatiallayer\layerfactory.h"

namespace VirtualMine
{
	namespace SpatialLayer
	{

		CLayerManager* CLayerManager::mpSingletonPtr(NULL);

		CLayerManager::CLayerManager()
			:ref_mpGeoSceneManager(NULL)
		{
			this->mpImageLayer = new VirtualMine::SpatialLayer::CImageLayer();
			this->mpDemLayer = new VirtualMine::SpatialLayer::CDemLayer();

			this->mpMineLayerMap = new CLayerManager::LayerMap();
			this->mpMineLayerMap->insert(std::pair<std::string,ILayer*>(this->mpImageLayer->getName(),this->mpImageLayer));
			this->mpMineLayerMap->insert(std::pair<std::string,ILayer*>(this->mpDemLayer->getName(),this->mpDemLayer));
		}

		CLayerManager::~CLayerManager()
		{
		
		}

		CLayerManager CLayerManager::getSingleton()
		{
			if(NULL == mpSingletonPtr)
				mpSingletonPtr = new CLayerManager();
			return *mpSingletonPtr;
		}

		CLayerManager* CLayerManager::getSingletonPtr()
		{
			if(NULL == mpSingletonPtr)
				mpSingletonPtr = new CLayerManager();
			return mpSingletonPtr;
		}

		void CLayerManager::setGeoSceneManager(VirtualMine::SpatialEngine::CGeoSceneManager * ref_pGeoSceneManager)
		{
			this->ref_mpGeoSceneManager = ref_pGeoSceneManager;
		}

		ILayer* CLayerManager::createLayer(const std::string& name)
		{
			return NULL;
		}

		ILayer* CLayerManager::createLayer(const std::string& name,const std::string& layerType)
		{
			CLayerFactory* pLayerFactory = dynamic_cast<CLayerFactory*>(CLayerFactoryRegister::getSingletonPtr()->getFactory(layerType));
			if (NULL != pLayerFactory)
			{
				ILayer* pLayer = pLayerFactory->createInstance(name);
				pLayer->setGeoSceneManager(this->ref_mpGeoSceneManager);

				mpMineLayerMap->insert(std::pair<std::string,ILayer*>(name,pLayer));
				return pLayer;
			}
			return NULL;
		}

		ILayer* CLayerManager::getImageLayer()
		{
			return this->mpImageLayer;
		}

		ILayer* CLayerManager::getDemoLayer()
		{
			return this->mpDemLayer;
		}

		CLayerManager::LayerMap* CLayerManager::getLayerMap()
		{
			return this->mpMineLayerMap;
		}
		
		void CLayerManager::renderToScene()
		{
			if (this->mpMineLayerMap->size() > 0)
			{
				CLayerManager::LayerMap::iterator bItr = this->mpMineLayerMap->begin();
				CLayerManager::LayerMap::iterator eItr = this->mpMineLayerMap->end();
				while (bItr != eItr)
				{
					bItr->second->draw();
					++bItr;
				}
			}
		}
		
		void CLayerManager::renderToScene(const std::string& name)
		{
			ILayer* pLayer = this->getLayer(name);
			if (NULL != pLayer)
			{
				pLayer->draw();
			}
		}


		ILayer* CLayerManager::getLayer(const std::string& name)
		{
			LayerMap::iterator itr = this->mpMineLayerMap->begin();
			if (itr != this->mpMineLayerMap->end())
			{
				return itr->second;
			}
			return NULL;
		}

		bool CLayerManager::destroyLayer(const std::string& name)
		{
			ILayer* pLayer = this->getLayer(name);
			if (pLayer)
			{
				CLayerFactory* pLayerFactory = dynamic_cast<CLayerFactory*>(CLayerFactoryRegister::getSingletonPtr()->getFactory(pLayer->getType()));
				if (NULL != pLayerFactory)
				{
					pLayerFactory->destroyInstance(pLayer);
				}
			}
			
			return false;
		}

	}
}
